import React, { useState, useEffect, useContext } from 'react';
// FIX: Added .js extension to local file import
import { AuthContext } from './authcontext.js';
// FIX: Added .css extension to local file import
import './ExpertPanel.css'; 

// Utility function to generate a readable ISO timestamp
const getTimestamp = () => new Date().toISOString();

const ExpertPanel = () => {
    const { user, signOut } = useContext(AuthContext);

    // --- NEW STATE STRUCTURE ---
    const [problem, setProblem] = useState('');
    const [cause, setCause] = useState('');
    const [steps, setSteps] = useState('');
    // -------------------------

    const [message, setMessage] = useState('');
    const [storedLogs, setStoredLogs] = useState([]);
    const [loading, setLoading] = useState(false);

    const expertId = user?.uid || 'expert_demo';
    const API_URL = 'http://127.0.0.1:8001';

    // Helper to fetch logs
    const fetchLogs = async () => {
        setLoading(true);
        try {
            const response = await fetch(`${API_URL}/get_logs/${expertId}`);
            if (!response.ok) {
                throw new Error('Failed to fetch logs');
            }
            const data = await response.json();
            // The backend /get_logs returns a structure like { logs: { documents: [...] } }
            // If the backend returns metadatas, we need to adapt this, but based on the code provided,
            // we'll stick to displaying the documents which combine all fields.
            const logDocuments = data.logs.documents || [];
            
            // To properly display the structured data, we'll try to fetch metadatas later, 
            // but for now, we process the document text if metadatas are not available easily.
            setStoredLogs(logDocuments.reverse());
            setLoading(false);

        } catch (error) {
            console.error("Error fetching logs:", error);
            setMessage(`Error fetching logs: ${error.message}`);
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchLogs();
    }, [expertId]);


    const handleLogSubmit = async (e) => {
        e.preventDefault();
        setMessage('');

        if (!problem.trim() || !cause.trim() || !steps.trim()) {
            setMessage('Please fill in all three fields (Problem, Cause, Steps).');
            return;
        }

        setLoading(true);

        // --- UPDATED PAYLOAD ---
        const logData = {
            expert_id: expertId,
            problem: problem.trim(),
            cause: cause.trim(),
            steps: steps.trim(),
            timestamp: getTimestamp(),
        };
        // -----------------------

        try {
            const response = await fetch(`${API_URL}/store_log`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(logData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.detail || 'Failed to store log on the server.');
            }

            const result = await response.json();
            setMessage(`‚úÖ Success! Log stored with ID: ${result.id}`);
            
            // Clear form fields after successful submission
            setProblem('');
            setCause('');
            setSteps('');

            fetchLogs(); // Refresh the list of stored logs

        } catch (error) {
            console.error('Submission Error:', error);
            setMessage(`üî¥ Error: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="expert-panel p-6 bg-gray-900 min-h-screen text-gray-100 font-inter">
            <header className="flex justify-between items-center pb-8 border-b border-gray-700">
                <h1 className="text-3xl font-bold flex items-center">
                    <span className="text-blue-400 mr-3">üõ†Ô∏è</span> Expert Knowledge Panel <span className="text-sm ml-2 text-gray-400">(Expert: {expertId})</span>
                </h1>
                <button 
                    onClick={signOut} 
                    className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg shadow-md transition duration-200"
                >
                    Sign out
                </button>
            </header>

            <section className="mt-8 mb-12 p-6 bg-gray-800 rounded-xl shadow-lg">
                <h2 className="text-2xl font-semibold mb-6 text-blue-300">Create New Knowledge Log</h2>
                
                <form onSubmit={handleLogSubmit} className="space-y-4">
                    
                    {/* 1. PROBLEM / QUERY */}
                    <div className="form-group">
                        <label htmlFor="problem" className="block text-gray-300 mb-1">Problem / Symptom</label>
                        <textarea
                            id="problem"
                            value={problem}
                            onChange={(e) => setProblem(e.target.value)}
                            rows="2"
                            placeholder="e.g., Elevator stops abruptly between floors and smells burnt."
                            className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 text-white resize-none"
                            disabled={loading}
                        ></textarea>
                    </div>

                    {/* 2. CAUSE */}
                    <div className="form-group">
                        <label htmlFor="cause" className="block text-gray-300 mb-1">Identified Cause</label>
                        <textarea
                            id="cause"
                            value={cause}
                            onChange={(e) => setCause(e.target.value)}
                            rows="2"
                            placeholder="e.g., Overloaded traction motor due to a failing bearing."
                            className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 text-white resize-none"
                            disabled={loading}
                        ></textarea>
                    </div>

                    {/* 3. FIXING STEPS */}
                    <div className="form-group">
                        <label htmlFor="steps" className="block text-gray-300 mb-1">Detailed Fixing Steps (One or more)</label>
                        <textarea
                            id="steps"
                            value={steps}
                            onChange={(e) => setSteps(e.target.value)}
                            rows="5"
                            placeholder="1. Lock out the main power circuit. 2. Inspect the motor housing for bearing lubricant leakage. 3. Replace the failing bearing with part #XYZ. 4. Run a full diagnostic test."
                            className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 text-white resize-none"
                            disabled={loading}
                        ></textarea>
                    </div>

                    <div className="flex items-center justify-between pt-2">
                        <button
                            type="submit"
                            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg shadow-md transition duration-200 disabled:opacity-50"
                            disabled={loading}
                        >
                            {loading ? 'Saving...' : 'Save to Knowledge Base'}
                        </button>
                        {message && (
                            <p className={`text-sm font-medium ${message.startsWith('‚úÖ') ? 'text-green-400' : 'text-red-400'}`}>
                                {message}
                            </p>
                        )}
                    </div>
                </form>
            </section>

            <section className="mt-12 p-6 bg-gray-800 rounded-xl shadow-lg">
                <h2 className="text-2xl font-semibold mb-6 text-blue-300 flex justify-between items-center">
                    View Stored Logs ({storedLogs.length} items)
                    <button 
                        onClick={fetchLogs} 
                        className="px-3 py-1 bg-gray-700 hover:bg-gray-600 text-sm text-gray-300 rounded-lg transition duration-200 disabled:opacity-50"
                        disabled={loading}
                    >
                        {loading ? 'Refreshing...' : 'Refresh Logs'}
                    </button>
                </h2>
                
                <div className="log-list space-y-4">
                    {storedLogs.length > 0 ? (
                        storedLogs.map((log, index) => (
                            <div key={index} className="p-4 bg-gray-700 rounded-lg shadow-sm">
                                <p className="font-semibold text-blue-300">Log #{storedLogs.length - index}</p>
                                <pre className="text-sm whitespace-pre-wrap mt-1 text-gray-300">{log}</pre>
                            </div>
                        ))
                    ) : (
                        <p className="text-gray-400">No knowledge logs found for expert: {expertId}.</p>
                    )}
                </div>
            </section>
        </div>
    );
};

export default ExpertPanel;
