import React from "react";

/**
 * Renders a single chat message bubble, formatting AI messages into steps.
 * @param {{ message: { role: string, text: string } }} props - Message object.
 */
export default function ChatMessage({ message }){
    const baseStyle = "max-w-[70%] p-3 rounded-xl shadow-md whitespace-pre-wrap transition duration-300";
    
    if(message.role === "user"){
        return (
            <div className="flex justify-end mb-4">
                <div className={`${baseStyle} bg-blue-600 text-white`}>
                    {message.text}
                </div>
            </div>
        );
    }

    const isAiResponse = message.role === "ai" || message.role === "assistant";
    
    if(isAiResponse){
        // Split the AI text by newline to create steps for display
        const steps = message.text.split('\n').filter(s => s.trim());

        // Simple loading message check
        if(steps.length === 0 || message.text.toLowerCase().includes('thinking')){
            return (
                <div className="flex justify-start mb-4">
                    <div className={`${baseStyle} bg-gray-200 text-gray-500 italic animate-pulse`}>
                        {message.text || "Searching expert knowledge..."}
                    </div>
                </div>
            );
        }

        return (
            <div className="flex justify-start mb-4">
                <div className={`${baseStyle} bg-gray-100 text-gray-800 border border-gray-200`}>
                    <p className="font-semibold mb-2 text-blue-800">Suggested Steps:</p>
                    <ol className="list-decimal list-inside space-y-2 text-sm pl-1">
                        {steps.map((s, idx) => (
                            <li key={idx} className="pl-1">{s.trim()}</li>
                        ))}
                    </ol>
                </div>
            </div>
        );
    }

    return null;
}
