import axios from "axios";

const BACKEND_URL = "http://127.0.0.1:8001";

/**
 * AI-powered vector + semantic search
 */
export const searchKnowledge = async (query, elevatorId = "") => {
    try {
        const url = `${BACKEND_URL}/knowledge_search?query=${encodeURIComponent(query)}${
            elevatorId ? `&elevator_id=${encodeURIComponent(elevatorId)}` : ""
        }`;

        const res = await axios.get(url);
        const results = res.data?.results || [];
        console.log("AI retrieval results:", results);
        return results;
    } catch (error) {
        console.error("❌ Error fetching knowledge:", error);
        return [
            {
                problem: "Error",
                cause: "Unable to reach backend or model.",
                steps: error.message,
                distance: 1.0
            },
        ];
    }
};
