import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from './authcontext';

// Expert Knowledge Base API runs on port 8000 (ChromaDB)
const EXPERT_API_BASE_URL = 'http://localhost:8000';

const ExpertPanel = () => {
    // AuthContext provides user (expert) details
    const { user, logout } = useContext(AuthContext);
    const expertId = user?.id || 'unknown_expert';

    // State for the new log form
    const [query, setQuery] = useState('');
    const [response, setResponse] = useState('');

    // State for viewing stored logs
    const [storedLogs, setStoredLogs] = useState([]);
    const [isLoadingLogs, setIsLoadingLogs] = useState(false);
    const [saveMessage, setSaveMessage] = useState('');

    // Function to fetch logs from the backend
    const fetchLogs = async () => {
        setIsLoadingLogs(true);
        try {
            // API Endpoint for fetching expert logs
            const response = await axios.get(`${EXPERT_API_BASE_URL}/get_logs/${expertId}`);
            
            // The backend (backend.py) returns an object with a 'logs' key, 
            // and the documents are under 'documents' array inside 'logs'.
            const docs = response.data?.logs?.documents || [];
            
            // Stored logs are just the document strings (the 'response' field we saved)
            setStoredLogs(docs);
            console.log(`Successfully fetched ${docs.length} logs for ${expertId}.`);
            
        } catch (error) {
            console.error('Failed to fetch stored logs:', error);
            // In case of error, set logs to empty array
            setStoredLogs([]); 
        } finally {
            setIsLoadingLogs(false);
        }
    };

    // Initial load of logs when the component mounts
    useEffect(() => {
        if (expertId && expertId !== 'unknown_expert') {
            fetchLogs();
        }
    }, [expertId]);

    // Function to handle log submission
    const handleSaveLog = async (e) => {
        e.preventDefault();
        setSaveMessage('Saving...');

        if (!query.trim() || !response.trim()) {
            setSaveMessage('Error: Both Query and Response are required.');
            return;
        }

        try {
            const logData = {
                expert_id: expertId,
                query: query.trim(),
                response: response.trim(),
            };

            // API Endpoint for saving expert logs (POST /store_log)
            const res = await axios.post(`${EXPERT_API_BASE_URL}/store_log`, logData);
            
            setSaveMessage(`Success! Log saved with ID: ${res.data.id}`);
            setQuery(''); // Clear form fields
            setResponse('');

            // After successful save, refresh the list of stored logs
            fetchLogs(); 

        } catch (error) {
            console.error('Error saving log to knowledge base:', error);
            if (error.response) {
                // Server responded with a status code outside the 2xx range
                setSaveMessage(`Error: ${error.response.data.detail || 'Server error.'}`);
            } else if (error.request) {
                // Request was made but no response received (CORS/Network error)
                setSaveMessage('Error: No response from backend (check if backend service on port 8000 is running).');
            } else {
                // Something else happened
                setSaveMessage('Error: Failed to send request.');
            }
        }
    };

    // Helper component to render each log entry
    const LogEntry = ({ content }) => (
        <div className="p-4 mb-3 bg-gray-700 rounded-lg shadow-md border border-gray-600">
            <pre className="whitespace-pre-wrap font-mono text-sm text-gray-300">{content}</pre>
        </div>
    );

    return (
        <div className="p-6 max-w-4xl mx-auto bg-gray-900 min-h-screen text-white">
            <header className="flex justify-between items-center pb-4 border-b border-gray-700 mb-6">
                <h1 className="text-2xl font-bold text-blue-400">
                    ðŸ”§ Expert Knowledge Panel (Expert: {expertId})
                </h1>
                <button 
                    onClick={logout} 
                    className="bg-red-600 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-lg transition duration-200"
                >
                    Sign Out
                </button>
            </header>

            <section className="mb-8 p-6 bg-gray-800 rounded-xl shadow-2xl">
                <h2 className="text-xl font-semibold mb-4 text-gray-200">Create New Knowledge Log</h2>
                <form onSubmit={handleSaveLog} className="space-y-4">
                    <div>
                        <label htmlFor="query" className="block text-sm font-medium mb-1 text-gray-400">Problem/Query Summary</label>
                        <input
                            id="query"
                            type="text"
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                            className="w-full p-3 border border-gray-600 rounded-lg bg-gray-700 text-white focus:ring-blue-500 focus:border-blue-500"
                            placeholder="e.g., Error Code 47: Door stuck open"
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="response" className="block text-sm font-medium mb-1 text-gray-400">Detailed Expert Solution/Response</label>
                        <textarea
                            id="response"
                            value={response}
                            onChange={(e) => setResponse(e.target.value)}
                            className="w-full p-3 h-32 border border-gray-600 rounded-lg bg-gray-700 text-white focus:ring-blue-500 focus:border-blue-500"
                            placeholder="Step-by-step solution..."
                            required
                        ></textarea>
                    </div>
                    
                    <div className="flex items-center justify-between">
                        <button
                            type="submit"
                            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg transition duration-200 shadow-lg"
                        >
                            Save to Knowledge Base
                        </button>
                        {saveMessage && (
                            <p className={`ml-4 text-sm font-medium ${saveMessage.startsWith('Error') ? 'text-red-400' : 'text-green-400'}`}>
                                {saveMessage}
                            </p>
                        )}
                    </div>
                </form>
            </section>

            <section>
                <h2 className="text-xl font-semibold mb-4 text-gray-200">
                    View Stored Logs ({storedLogs.length} items)
                </h2>
                
                <button 
                    onClick={fetchLogs} 
                    disabled={isLoadingLogs}
                    className="bg-gray-600 hover:bg-gray-700 text-white font-semibold py-2 px-4 rounded-lg transition duration-200 mb-4 disabled:opacity-50"
                >
                    {isLoadingLogs ? 'Loading...' : 'Refresh Logs'}
                </button>
                
                <div className="space-y-3">
                    {isLoadingLogs ? (
                        <p className="text-gray-400">Loading knowledge base entries...</p>
                    ) : storedLogs.length > 0 ? (
                        storedLogs.map((log, index) => (
                            <LogEntry key={index} content={log} />
                        ))
                    ) : (
                        <p className="text-gray-400">No logs found for this expert. Try saving one above!</p>
                    )}
                </div>
            </section>
        </div>
    );
};

export default ExpertPanel;
