import React, { useState, useEffect } from 'react';
// NOTE: In a real app running in a browser environment, 
// you would ensure ExpertPanel.css is linked in your index.html.

// --- MOCKED AUTH HOOK ---
// Mocking the auth context for a single-file React environment.
const useAuth = () => {
    // Return a default expert user for functionality testing
    const [user, setUser] = useState({ uid: 'EXPERT_12345' });

    const signOut = () => {
        // In a real app, this would handle Firebase sign-out
        console.log("Mock Sign Out triggered.");
        setUser(null);
    };

    return { user, signOut };
};
// --- END MOCKED AUTH HOOK ---

// Utility function to generate a readable ISO timestamp
const getTimestamp = () => new Date().toISOString();

// Utility for exponential backoff (crucial for API calls)
const fetchDataWithRetry = async (url, options, retries = 3) => {
    for (let i = 0; i < retries; i++) {
        try {
            const response = await fetch(url, options);
            if (!response.ok) {
                // Throw an error for non-200 responses
                const errorText = await response.text();
                throw new Error(`Server Error (${response.status}): ${errorText.substring(0, 100)}...`);
            }
            return response;
        } catch (error) {
            if (i < retries - 1) {
                // Exponential backoff wait: 1s, 2s, 4s
                const delay = Math.pow(2, i) * 1000;
                // console.warn(`Fetch attempt ${i + 1} failed. Retrying in ${delay}ms...`);
                await new Promise(resolve => setTimeout(resolve, delay));
            } else {
                throw error; // Throw final error
            }
        }
    }
};


const ExpertPanel = () => {
    const { user, signOut } = useAuth();
    const [problem, setProblem] = useState('');
    const [cause, setCause] = useState('');
    const [steps, setSteps] = useState('');
    const [message, setMessage] = useState('');
    const [storedLogs, setStoredLogs] = useState([]);
    const [loading, setLoading] = useState(false);

    const expertId = user?.uid || 'expert_demo';
    // Placeholder URL for API interactions. In a real environment, this would be an actual endpoint.
    const API_URL = 'https://api.example.com/knowledge-base'; 

    const fetchLogs = async () => {
        if (!user) return;

        setLoading(true);
        setMessage('Fetching logs...');

        try {
            // NOTE: Since the backend is mocked, we rely on the mock data fallback below.
            // If this were a real application, we would use the fetchDataWithRetry call.
            
            // Simulating API latency
            await new Promise(resolve => setTimeout(resolve, 800));

            let logsToDisplay = [];
            
            // Mock Data structure based on the previous implementation's expectation
            if (expertId === 'EXPERT_12345') {
                 logsToDisplay = [
                    { id: 'mock-1', problem: 'Server error 503 during deployment phase', cause: 'High load or bad gateway timeout. Needs proper monitoring.', steps: '1. Check power. 2. Restart services. 3. Monitor load.', timestamp: new Date(Date.now() - 3600000).toLocaleString() },
                    { id: 'mock-2', problem: 'Motor overheating at 80% throttle. Customer reported smoke.', cause: 'Cooling fan failure due to debris blocking intake filter.', steps: '1. Shut down motor. 2. Clear fan intake. 3. Replace fan assembly. 4. Run diagnostics.', timestamp: new Date(Date.now() - 7200000).toLocaleString() },
                    { id: 'mock-3', problem: 'Database connection failed unexpectedly, showing error code 403.', cause: 'Expired API token used for database service account. No automatic rotation enabled.', steps: '1. Generate new API key/token. 2. Update all client configurations and redeploy. 3. Set up a rotation schedule.', timestamp: new Date(Date.now() - 10800000).toLocaleString() },
                 ].reverse();
            }

            setStoredLogs(logsToDisplay);
            setMessage(`Successfully loaded ${logsToDisplay.length} expert logs.`);

        } catch (error) {
            console.error("Error fetching logs:", error);
            setMessage(`üî¥ Error fetching logs: ${error.message.substring(0, 60)}...`); 
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (user) {
            fetchLogs();
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [user]);


    const handleLogSubmit = async (e) => {
        e.preventDefault();
        setMessage('');

        if (!user) {
            setMessage('üî¥ Error: You must be signed in to submit logs.');
            return;
        }

        if (!problem.trim() || !cause.trim() || !steps.trim()) {
            setMessage('üî¥ Please fill in all three required fields (Problem, Cause, Steps).');
            return;
        }

        setLoading(true);

        const logData = {
            expert_id: expertId, 
            problem: problem.trim(),
            cause: cause.trim(),
            steps: steps.trim(),
            timestamp: getTimestamp(),
        };

        try {
            // Mocking the API call, assuming success
            // In a real app, this would be: await fetchDataWithRetry(`${API_URL}/store_log`, { ... })
            await new Promise(resolve => setTimeout(resolve, 1500)); 

            setMessage(`‚úÖ Success! Log stored (Mock ID: ${Date.now()}).`);
            
            setProblem('');
            setCause('');
            setSteps('');

            fetchLogs(); // Refresh the list of stored logs

        } catch (error) {
            console.error('Submission Error:', error);
            setMessage(`üî¥ Error submitting log: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    const statusClass = message.startsWith('‚úÖ') ? 'status-success' : (message.startsWith('üî¥') ? 'status-error' : '');
    const StatusIcon = message.startsWith('‚úÖ') ? '‚úÖ' : (message.startsWith('üî¥') ? 'üî¥' : (loading ? '... ' : ''));

    return (
        <div className="expert-panel">
            {/* --- HEADER --- */}
            <header className="header">
                <h1>
                    <span style={{marginRight: '15px'}}>üõ†Ô∏è</span> Expert Knowledge Panel 
                    <span className="expert-id">(ID: {expertId})</span>
                </h1>
                <button 
                    onClick={signOut} 
                    className="sign-out-button"
                >
                    {user ? 'Sign Out' : 'Sign In (Mock)'}
                </button>
            </header>

            {/* --- KNOWLEDGE ENTRY SECTION --- */}
            <section className="section-card">
                <h2>Submit New Expert Log</h2>
                
                <form onSubmit={handleLogSubmit}>
                    
                    {/* 1. PROBLEM / QUERY */}
                    <div className="form-group">
                        <label htmlFor="problem">
                            1. Problem / Symptom 
                            <span className="required-star">*</span>
                        </label>
                        <textarea
                            id="problem"
                            value={problem}
                            onChange={(e) => setProblem(e.target.value)}
                            rows="4"
                            placeholder="e.g., Elevator stops abruptly between floors and smells burnt. Customer reported a loud clicking sound just before the stop."
                            disabled={loading || !user}
                        ></textarea>
                    </div>

                    {/* 2. CAUSE */}
                    <div className="form-group">
                        <label htmlFor="cause">
                            2. Identified Cause 
                            <span className="required-star">*</span>
                        </label>
                        <textarea
                            id="cause"
                            value={cause}
                            onChange={(e) => setCause(e.target.value)}
                            rows="5"
                            placeholder="e.g., The main contactor coil for the traction motor overheated and failed, leading to a temporary short. Clicking sound was the contactor failing to engage."
                            disabled={loading || !user}
                        ></textarea>
                    </div>

                    {/* 3. FIXING STEPS */}
                    <div className="form-group">
                        <label htmlFor="steps">
                            3. Detailed Fixing Steps (Action Plan) 
                            <span className="required-star">*</span>
                        </label>
                        <textarea
                            id="steps"
                            value={steps}
                            onChange={(e) => setSteps(e.target.value)}
                            rows="8"
                            placeholder="1. Lock out/Tag out the main power (LOTO). 2. Confirm failed contactor (CR1) by checking resistance. 3. Replace contactor CR1 (Part # XYZ-456) and associated wiring harness. 4. Run empty car through a full cycle. 5. Clear fault codes and finalize paperwork."
                            disabled={loading || !user}
                        ></textarea>
                    </div>

                    <div className="submit-area">
                        <button
                            type="submit"
                            className="submit-button"
                            disabled={loading || !user}
                        >
                            {loading ? 'Saving Knowledge...' : 'Save to Knowledge Base'}
                        </button>
                        {message && (
                            <p className={`status-message ${statusClass}`}>
                                {StatusIcon} {message.replace('‚úÖ Success! ', '').replace('üî¥ Error: ', '')}
                            </p>
                        )}
                    </div>
                </form>
            </section>

            {/* --- STORED LOGS SECTION --- */}
            <section className="section-card">
                <div className="log-controls">
                    <h2>Past Knowledge Logs ({storedLogs.length} items)</h2>
                    <button 
                        onClick={fetchLogs} 
                        className="refresh-button"
                        disabled={loading || !user}
                    >
                        <svg className={`refresh-icon ${loading ? 'animate-spin' : ''}`} xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                           <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m1.582 0a8 8 0 0115.356 2m-1.87 0a6 6 0 00-11.455 1m-2.905-1.556L4 12V5a1 1 0 011-1h6a1 1 0 011 1v6a1 1 0 01-1 1H5a1 1 0 01-1-1v-4zm6 0h4m-2 0V4" />
                        </svg>
                        {loading ? 'Refreshing...' : 'Refresh Logs'}
                    </button>
                </div>
                
                <div className="log-list">
                    {storedLogs.length > 0 ? (
                        storedLogs.map((log) => (
                            <div key={log.id} className="log-item">
                                <p className="log-item-header">
                                    <span>Problem: {log.problem.substring(0, 70)}{log.problem.length > 70 ? '...' : ''}</span>
                                    <span className="log-timestamp">{log.timestamp}</span>
                                </p>
                                <div className="log-item-details">
                                    <p><strong>Cause:</strong> {log.cause.substring(0, 100)}{log.cause.length > 100 ? '...' : ''}</p>
                                    <p><strong>Steps:</strong> {log.steps.substring(0, 120)}...</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p className="no-logs">
                            {loading ? 'Loading logs...' : 'No knowledge logs found for this expert yet. Start adding solutions above!'}
                        </p>
                    )}
                </div>
            </section>
        </div>
    );
};

export default ExpertPanel;
