import React, { useState, useEffect } from 'react';
import axios from "axios";

// --- MOCKED AUTH HOOK ---
// This mock ensures the component has a user ID for the API calls.
const useAuth = () => {
    // Return a default expert user for functionality testing
    // Using the ID from your original code ('expert123') for consistency.
    const [user, setUser] = useState({ uid: 'expert123' });

    const signOut = () => {
        // In a real app, this would handle Firebase sign-out
        console.log("Mock Sign Out triggered.");
        setUser(null);
    };

    return { user, signOut };
};
// --- END MOCKED AUTH HOOK ---

// Utility function to generate a readable ISO timestamp
const getTimestamp = () => new Date().toISOString();


const ExpertPanel = () => {
    // Retaining state variables from your original file
    const [query, setQuery] = useState('');
    const [response, setResponse] = useState('');
    const [logs, setLogs] = useState([]); // Array of raw log documents
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);
    
    const { user, signOut } = useAuth();
    const expertId = user?.uid || 'expert_demo';
    
    // Using the API URL from your original code (port 8000)
    const API_URL = 'http://localhost:8000'; 

    
    // --- Log Fetching Logic (using axios) ---
    const fetchLogs = async () => {
        if (!user) return;

        setLoading(true);
        setMessage('');

        try {
            // Fetch logs using the expert's ID
            const res = await axios.get(`${API_URL}/get_logs/${expertId}`);
            
            // Assume the backend returns an array of documents (strings) in res.data.logs.documents
            const documents = res.data?.logs?.documents || [];
            
            // NOTE: Since your original structure returns an array of strings/documents
            // and not structured metadata, we display them as raw text logs.
            setLogs(documents.reverse()); // Show newest first

        } catch (error) {
            console.error("Error fetching logs:", error);
            // Use the message state for visible feedback instead of alert()
            setMessage(`üî¥ Error fetching logs: ${error.message.substring(0, 60)}...`); 
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        // Fetch logs only when the user object is confirmed
        if (user) {
            fetchLogs();
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [user]);


    // --- Log Submission Logic (using axios) ---
    const handleLogSubmit = async (e) => {
        e.preventDefault();
        setMessage('');

        if (!user) {
            setMessage('üî¥ Error: You must be signed in to submit logs.');
            return;
        }

        if (!query.trim() || !response.trim()) {
            setMessage('Please fill in both fields.');
            return;
        }

        setLoading(true);

        const logData = {
            expert_id: expertId, 
            // Mapping your original fields to the structured fields the backend expects
            problem: query.trim(),
            fix: response.trim(),
            timestamp: getTimestamp(),
        };

        try {
            // Using axios.post for submission
            const res = await axios.post(`${API_URL}/store_log`, logData);

            // Accessing the ID from the axios response
            setMessage(`‚úÖ Success! Log stored with ID: ${res.data.id || 'N/A'}`);
            
            setQuery('');
            setResponse('');

            fetchLogs(); // Refresh the list of stored logs

        } catch (error) {
            console.error('Submission Error:', error);
            setMessage(`üî¥ Error submitting log: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    // Component for a styled text area group (Uses Tailwind CSS)
    const StyledTextArea = ({ id, label, value, onChange, placeholder, rows, icon }) => (
        <div className="form-group rounded-2xl p-5 bg-gray-800 border border-gray-700 shadow-xl transition duration-300 transform hover:scale-[1.005] hover:shadow-blue-500/10 focus-within:ring-2 focus-within:ring-blue-500">
            <label htmlFor={id} className="block text-blue-300 mb-3 font-extrabold text-lg flex items-center">
                <span className="mr-3 text-2xl leading-none">{icon}</span> {label}
            </label>
            <textarea
                id={id}
                value={value}
                onChange={onChange}
                rows={rows}
                placeholder={placeholder}
                className="w-full p-4 bg-gray-950 border border-gray-600 rounded-lg focus:ring-4 focus:ring-blue-500 focus:border-blue-500 text-white resize-none shadow-inner transition duration-200 outline-none placeholder-gray-500"
                disabled={loading || !user}
            ></textarea>
        </div>
    );

    // Simple Card for displaying the raw log string
    const LogCard = ({ log, index }) => (
        <div className="log-card p-4 bg-gray-800 rounded-xl border border-gray-700 shadow-lg space-y-2">
            <span className="text-xs font-mono text-gray-400">Log Entry #{logs.length - index}</span>
            <p className="text-gray-300 text-sm whitespace-pre-wrap">{log}</p>
        </div>
    );

    return (
        <div className="expert-panel p-4 md:p-8 bg-gray-900 min-h-screen text-gray-100 font-inter">
            <header className="flex flex-col md:flex-row justify-between items-start md:items-center pb-6 border-b border-blue-600 mb-8">
                <h1 className="text-3xl font-extrabold text-blue-400 flex items-center mb-4 md:mb-0">
                    <span className="mr-3 text-4xl">‚öôÔ∏è</span> Expert Knowledge Panel <span className="text-base ml-4 font-normal text-gray-400">(ID: {expertId})</span>
                </h1>
                <button 
                    onClick={signOut} 
                    className={`px-4 py-2 text-white font-medium rounded-full shadow-lg transition duration-300 transform ${user ? 'bg-red-600 hover:bg-red-500 active:scale-95' : 'bg-green-600 hover:bg-green-500 active:scale-95'}`}
                >
                    {user ? 'Sign Out' : 'Sign In (Mock)'}
                </button>
            </header>

            {/* --- KNOWLEDGE ENTRY SECTION (Stylized) --- */}
            <section className="mb-12 p-6 bg-gray-800 rounded-2xl shadow-2xl border border-blue-600/20">
                <h2 className="text-3xl font-bold mb-6 text-blue-300 border-b border-gray-700 pb-3">Submit New Expert Log</h2>
                
                <form onSubmit={handleLogSubmit} className="space-y-8">
                    
                    {/* 1. PROBLEM / QUERY */}
                    <StyledTextArea
                        id="query"
                        label="1. Problem / Query / Symptom"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        rows="3"
                        placeholder="e.g., Elevator stops abruptly between floors and smells burnt. Customer reported a loud clicking sound."
                        icon="üßê"
                    />

                    {/* 2. EXPERT RESPONSE / FIX */}
                    <StyledTextArea
                        id="response"
                        label="2. Expert Response / Detailed Fix"
                        value={response}
                        onChange={(e) => setResponse(e.target.value)}
                        rows="7"
                        placeholder="e.g., The main contactor coil (CR1) failed due to overheating. Replaced contactor CR1 (Part # XYZ-456) and associated wiring harness. Confirmed full cycle run."
                        icon="‚úÖ"
                    />

                    <div className="flex flex-col md:flex-row items-start md:items-center justify-between pt-4">
                        <button
                            type="submit"
                            // Enhanced button with gradient and better shadow
                            className="px-8 py-4 bg-gradient-to-r from-blue-600 to-blue-800 text-white font-extrabold rounded-full shadow-2xl shadow-blue-800/50 transition duration-300 transform hover:scale-[1.03] disabled:opacity-50 disabled:hover:scale-100 mb-4 md:mb-0"
                            disabled={loading || !user}
                        >
                            {loading ? 'Saving Knowledge...' : 'Save to Knowledge Base'}
                        </button>
                        {message && (
                            <p className={`text-base font-semibold p-2 rounded-xl ${message.startsWith('‚úÖ') ? 'text-green-400 bg-green-900/50' : 'text-red-400 bg-red-900/50'}`}>
                                {message}
                            </p>
                        )}
                    </div>
                </form>
            </section>

            {/* --- STORED LOGS SECTION --- */}
            <section className="mt-12 p-6 bg-gray-800 rounded-2xl shadow-2xl border border-blue-600/20">
                <div className="flex justify-between items-center border-b border-gray-700 pb-3 mb-6">
                    <h2 className="text-3xl font-bold text-blue-300">
                        Past Knowledge Logs ({logs.length} items)
                    </h2>
                    <button 
                        onClick={fetchLogs} 
                        className="px-4 py-2 bg-gray-700 hover:bg-gray-600 text-sm text-gray-300 font-medium rounded-full transition duration-200 disabled:opacity-50"
                        disabled={loading || !user}
                    >
                        {loading ? 'Refreshing...' : 'Refresh Logs'}
                    </button>
                </div>
                
                <div className="log-list space-y-4 max-h-[600px] overflow-y-auto pr-2">
                    {logs.length > 0 ? (
                        logs.map((log, index) => (
                            <LogCard key={index} log={log} index={index} />
                        ))
                    ) : (
                        <p className="text-gray-400 text-center py-6">
                            {loading ? 'Loading logs...' : 'No knowledge logs found for this expert yet. Use the form above to add your first log!'}
                        </p>
                    )}
                </div>
            </section>
        </div>
    );
};

export default ExpertPanel;
