import React, { useEffect } from 'react';
import ChatPanel from './ChatPanel'; 

// --- MOCK AUTH/ROUTER for standalone simplicity ---
const useAuth = () => ({ user: { id: 'tech-007', role: 'technician' }, logout: () => console.log("Mock Logout") });
const useNavigate = () => (path) => console.log(`[Mock Router] Navigating to: ${path}`);
// ---

/**
 * The main page component for the Technician Chat Interface.
 */
export default function ChatPage() {
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    // Mock check authentication and role
    useEffect(() => {
        if (!user || user.role !== 'technician') {
            // navigate('/login'); // Uncomment in a real app
        }
    }, [user, navigate]);

    const handleSignOut = () => {
        logout();
        navigate('/');
    };

    if (!user) {
        return <div className="min-h-screen bg-gray-100 p-10 text-center">Loading or authentication failed...</div>;
    }

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col font-sans">
            {/* Page Header */}
            <header className="flex justify-between items-center p-4 bg-white shadow-md flex-shrink-0">
                <h1 className="text-3xl font-extrabold text-blue-700 flex items-center">
                    <svg className="w-8 h-8 mr-2 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.066 12.066 0 002 12c0 5.06 3.07 9.428 7.398 11.232a.965.965 0 00.202.01c.214 0 .417-.06.6-.17l.092-.05A12.072 12.072 0 0012 22c2.812 0 5.437-1.118 7.398-3.04a12.066 12.066 0 002-7.056c0-4.095-2.022-7.85-5.618-9.988z"></path></svg>
                    RAG Tech Console
                </h1>
                <button 
                    onClick={handleSignOut} 
                    className="px-4 py-2 bg-red-500 text-white font-medium rounded-lg hover:bg-red-600 transition shadow"
                >
                    Sign out ({user.id})
                </button>
            </header>

            {/* Main Content Area */}
            <main className="flex-grow h-0">
                <ChatPanel /> 
            </main>
        </div>
    );
}
