import React, { useState } from "react";
import axios from "axios";

export default function ExpertPanel() {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState("");
  const [logs, setLogs] = useState([]);
  const expertId = "expert123"; // Replace with actual ID after login

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!query || !response) return alert("Please fill both fields");

    try {
      await axios.post("http://localhost:8000/store_log", {
        expert_id: expertId,
        query,
        response,
      });
      alert("✅ Log stored in ChromaDB successfully!");
      setQuery("");
      setResponse("");
    } catch (err) {
      console.error(err);
      alert("❌ Error storing log.");
    }
  };

  const loadLogs = async () => {
    try {
      const res = await axios.get(`http://localhost:8000/get_logs/${expertId}`);
      setLogs(res.data.logs.documents || []);
    } catch (err) {
      console.error(err);
      alert("Error loading logs");
    }
  };

  // ✅ THIS return is INSIDE the ExpertPanel function
  return (
    <div style={{ padding: "20px" }}>
      <h2>Expert Panel</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Problem or query..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={{ display: "block", width: "100%", marginBottom: "10px" }}
        />
        <textarea
          placeholder="Expert response or fix..."
          value={response}
          onChange={(e) => setResponse(e.target.value)}
          style={{ display: "block", width: "100%", marginBottom: "10px" }}
        />
        <button type="submit">Save to ChromaDB</button>
      </form>

      <hr />

      <button onClick={loadLogs}>View Stored Logs</button>
      <ul>
        {logs.map((log, i) => (
          <li key={i}>{log}</li>
        ))}
      </ul>
    </div>
  );
}
