import React, { useEffect, useState } from 'react';
import ChatBox from './ChatBox';

// --- MOCK AUTH/ROUTER for standalone simplicity ---
const useAuth = () => ({ user: { id: 'tech123', role: 'technician' }, logout: () => console.log("Mock Logout") });
const useNavigate = () => (path) => console.log(`[Mock Router] Navigating to: ${path}`);
// ---

export default function ChatPage() {
  const { user, logout } = useAuth();
  const [logs, setLogs] = useState([]); 
  const [active, setActive] = useState(null); 
  const [elevatorId, setElevatorId] = useState(""); // ‚úÖ to track currently entered elevator ID
  const nav = useNavigate();

  // üß† Fetch logs when Elevator ID changes
  useEffect(() => {
    async function fetchLogs() {
      if (!elevatorId.trim()) {
        setLogs([]);
        return;
      }

      try {
        const res = await fetch(
          `http://127.0.0.1:8001/get_logs/${user.id}?elevator_id=${encodeURIComponent(elevatorId)}`
        );
        const data = await res.json();

        if (data.logs && data.logs.metadatas) {
          const formattedLogs = data.logs.metadatas.map((meta, i) => ({
            log_id: data.logs.ids[i],
            user_query: meta.problem || "Unknown Problem",
            ai_response: meta.steps || "No response recorded",
            cause: meta.cause || "No cause mentioned",
            timestamp: meta.timestamp,
          }));
          setLogs(formattedLogs.reverse());
        } else {
          setLogs([]);
        }
      } catch (err) {
        console.error("‚ùå Error fetching logs:", err);
        setLogs([]);
      }
    }

    fetchLogs();
  }, [elevatorId, user.id]);

  // ‚úÖ This function will be called by ChatBox whenever elevatorId changes
  function handleElevatorIdChange(id) {
    setElevatorId(id);
  }

  // ‚úÖ When user clicks on a log to view it in ChatBox
  function onSelect(log) {
    setActive(log);
  }

  // ‚úÖ When ChatBox saves a new log, reload elevator logs
  async function handleNewLog() {
    if (elevatorId.trim()) {
      try {
        const res = await fetch(
          `http://127.0.0.1:8001/get_logs/${user.id}?elevator_id=${encodeURIComponent(elevatorId)}`
        );
        const data = await res.json();

        if (data.logs && data.logs.metadatas) {
          const formattedLogs = data.logs.metadatas.map((meta, i) => ({
            log_id: data.logs.ids[i],
            user_query: meta.problem || "Unknown Problem",
            ai_response: meta.steps || "No response recorded",
            cause: meta.cause || "No cause mentioned",
            timestamp: meta.timestamp,
          }));
          setLogs(formattedLogs.reverse());
        }
      } catch (err) {
        console.error("‚ùå Error refreshing logs:", err);
      }
    }
    setActive(null);
  }

  // ‚úÖ Start new search ‚Äî clear selection
  const handleNewSearch = () => {
    setActive(null);
  };

  // ‚úÖ Sign out
  const handleSignOut = () => {
    logout();
    nav('/');
  };

  if (!user) return null;

  return (
    <div className="page-container">
      {/* --- HEADER --- */}
      <div className="header">
        <div className="title">
          <span style={{ color: 'var(--accent-color)' }}>üõ†Ô∏è</span> Tech Console (DB Retrieval Only)
        </div>
        <div style={{ display: "flex", gap: '12px' }}>
          <div style={{ marginRight: '10px', color: 'var(--text-color)' }}>
            Tech ID: {user.id}
          </div>
          <button onClick={handleSignOut} className="btn outline">
            Sign out
          </button>
        </div>
      </div>

      {/* --- MAIN CONTENT --- */}
      <div className="page">
        {/* LEFT COLUMN - PREVIOUS LOGS */}
        <div className="left-col">
          <h3 style={{ marginTop: 0 }}>Previous Logs</h3>
          <button
            onClick={handleNewSearch}
            className="btn"
            style={{ marginBottom: '10px' }}
          >
            + Start New Search
          </button>

          <div
            className="log-list"
            style={{ maxHeight: 'calc(100vh - 200px)', overflowY: 'auto' }}
          >
            {logs.length === 0 ? (
              <p style={{ color: 'gray', textAlign: 'center' }}>
                {elevatorId
                  ? "No logs found for this Elevator ID."
                  : "Enter an Elevator ID to view logs."}
              </p>
            ) : (
              logs.map((l) => (
                <div
                  key={l.log_id}
                  className={`log-card ${
                    active && active.log_id === l.log_id ? 'active' : ''
                  }`}
                  onClick={() => onSelect(l)}
                  style={{
                    backgroundColor:
                      active && active.log_id === l.log_id
                        ? '#e0f2fe'
                        : 'white',
                    border: '1px solid #ddd',
                    padding: '10px',
                    borderRadius: '8px',
                    marginBottom: '10px',
                    cursor: 'pointer',
                  }}
                >
                  <h4>
                    {l.user_query.substring(0, 40)}
                    {l.user_query.length > 40 ? '...' : ''}
                  </h4>
                  <p style={{ color: '#475569', fontSize: '0.9em' }}>
                    Cause: {l.cause.substring(0, 50)}...
                  </p>
                  <p style={{ fontSize: '0.8em', color: '#64748b' }}>
                    {new Date(l.timestamp).toLocaleString()}
                  </p>
                </div>
              ))
            )}
          </div>
        </div>

        {/* RIGHT COLUMN - CHAT BOX */}
        <div className="right-col">
          <ChatBox
            expertId={user.id}
            initialLog={active}
            onNewLog={handleNewLog}
            onElevatorIdChange={handleElevatorIdChange} // ‚úÖ Pass elevator ID handler
          />
        </div>
      </div>
    </div>
  );
}
