import React, { useEffect, useState } from "react";
import axios from "axios";
import ChatBox from "./ChatBox";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./AuthContext";

// --- CONFIGURATION ---
const BACKEND_URL = "http://localhost:8001"; // Technician/Chat Service
// ---

export default function ChatPage(){
  const { user, logout } = useAuth();
  const [logs, setLogs] = useState([]);
  const [active, setActive] = useState(null);
  const [isLoadingLogs, setIsLoadingLogs] = useState(true);
  const nav = useNavigate();

  // Redirect if not logged in as a technician
  useEffect(() => {
    if (!user.id && user.role !== 'technician') {
      nav('/login?role=technician');
    }
  }, [user.id, user.role, nav]);


  // Function to fetch logs from the backend
  async function fetchLogs() {
    const expertIdToFetch = user.id; 
    if (!expertIdToFetch) return;

    setIsLoadingLogs(true);
    try {
      // Fetches logs associated with the logged-in user ID
      const url = `${BACKEND_URL}/api/get_logs/${expertIdToFetch}`; 
      const response = await axios.get(url);
      
      setLogs(response.data); 

    } catch (error) {
      console.error("Error loading logs from backend:", error);
      setLogs([]); 
    } finally {
      setIsLoadingLogs(false);
    }
  }

  // Load logs on component mount or when the user ID changes
  useEffect(()=>{
    if(user.id) {
      fetchLogs();
    }
  },[user.id]);

  function onSelect(log){
    setActive(log);
  }

  // Function passed to ChatBox to trigger a refresh after a new log is saved
  function refreshLogs() {
    fetchLogs();
  }
  
  const handleSignOut = () => {
    logout();
    nav('/');
  }

  // Render nothing if not logged in yet (or if redirecting)
  if (!user.id || user.role !== 'technician') {
    return null; 
  }

  return (
    <>
      <div className="header">
        <div className="title">ðŸš€ Elevator Support Assistant (Technician: {user.id})</div>
        <div style={{display:"flex", gap:12}}>
          <button className="btn outline" onClick={handleSignOut}>Sign out</button>
        </div>
      </div>

      <div className="page">
        <div className="left-col">
          <h3 style={{marginTop:0}}>Previous Logs</h3>
          {isLoadingLogs ? (
            <p>Loading logs...</p>
          ) : logs.length === 0 ? (
            <p>No logs found. Start a new query!</p>
          ) : (
            logs.map(l => (
              <div 
                key={l.log_id || l.id} 
                className={`log-card ${active && (active.log_id || active.id) === (l.log_id || l.id) ? 'active' : ''}`} 
                onClick={()=>onSelect(l)}
              >
                <h4>{l.user_query || "Untitled Query"}</h4>
                <p>{l.ai_response ? `${l.ai_response.slice(0, 80)}${l.ai_response.length > 80 ? "..." : ""}` : "No steps recorded."}</p>
              </div>
            ))
          )}
        </div>

        <div className="right-col">
          <div className="chat-top">
            <div>
              <strong>{active ? (active.user_query) : "Ask a query or pick a previous log"}</strong>
            </div>
          </div>

          {/* Pass the logged-in user's ID to ChatBox */}
          <ChatBox initialLog={active} onNewLog={refreshLogs} expertId={user.id} />
        </div>
      </div>
    </>
  );
}
